import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const agregar =[]
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);

  const colu = (e, og) =>  {
    if(og == 0){


      setColumns(agregar)
    }
    else {
      let empezamos = 0
      while(og != empezamos){
        agregar.push({
          title: "Nodo " + (empezamos+1),
          dataIndex: "porfavor",
          key: 'ya',
          render: (porfavor, row, index) => { return <input  type="number"/> }
        })
        empezamos++;
      }
      setColumns(agregar)

    }
  }

  const numeros = (e)=>{
    const numeros = parseInt(form.nodos,10);
    if(numeros > 0){
      colu(e,numeros);
      crearRow(numeros)
    }
  }

  const crearRow = (nod) =>{
    let newData = [];
    for(let i = 0; i< nod; i++){
      let fila = {};
      for(let j = 0; j < nod; j++){
        fila["porfavor"] = "3";
      }
      newData.push(fila);
      console.log(newData)
    }
    setData(newData);
  }


    const camino =  (t,v) =>{
      if(t[v] != 1) {
        camino(t,t[v]);
        console.log("- ",t[v]);
      }
    }

    const Dijkstra =  (w) =>
    {    let l = new Array(15+1),  t = new Array (15+1),   r = new Array(15+1),  min,  vmin;
       // el arreglo L guardar� los costos de los caminos m�s cortos del nodo 1 al nodo i seg�n se vayan acumulando
       // el arreglo T guarda el �ndice de un nodo de tal manera que el arco T[i] a i es el �ltimo en el camino m�s corto del nodo 1 al i.
       // el arreglo R guardar� el resultado del camino m�s corto del nodo 1 al i.
       // La l�gica de este algoritmo es la misma de Prim, pero acumulando caminos
       for (let i = 2; i<=15; i++){
          l[i] = w[0][i-1];
          t[i] = 1;
        }
       for (let x = 1; x < 15; x++)
       {	min = 999;
          for (let i = 2; i<= 15; i++){
            if ( 0 <= l[i] && l[i] <= min)
              {  min = l[i]; vmin = i; }
           }
         	for (let i=2; i<= 15; i++){
  	        if (l[vmin]+w[vmin-1][i-1] < l[i]) //aqui es donde se considera acumular caminos
               {	l[i] = l[vmin]+w[vmin-1][i-1];
  				        l[i] = vmin; }
           }
          r[vmin] = l[vmin];
          l[vmin] = -1;
       }
       cout << "\nCAMINOS MAS CORTOS\n"; // para desplegar todos los caminos resultantes a partir del nodo 1
       for (let i = 2; i <= 15; i++)
       { cout << "de 1 a " << i << ": 1";
         camino(T, i);
         cout << "-" << i << " cuesta " << r[i] << " unidades\n";
       }
     }

  let anchos = 	[[0, 2, 3, 2, 999, 4, 5, 2, 9, 999, 3, 2, 999, 5, 6],
  							[2, 0, 2, 8, 999, 5, 999, 4, 6, 999, 8, 2, 3, 4, 1],
  							[3, 2, 0, 1, 6, 3, 999, 5, 6, 1, 999, 4, 2, 7, 999],
  							[2, 8, 1, 0, 1, 6, 1, 3, 3, 3, 999, 8, 1, 4, 999],
  							[999, 999, 6, 1, 0, 999, 1, 4, 1, 999, 3, 1, 999, 1, 999],
  							[4, 5, 3, 6, 999, 0, 999, 7, 1, 7, 6, 999, 2, 7, 4],
  							[5, 999, 999, 1, 1, 999, 0, 4, 1, 4, 7, 4, 999, 1, 5],
  							[2, 4, 5, 3, 4, 7, 4, 0, 2, 999, 4, 999, 1, 4, 1],
  							[9, 6, 6, 3, 1, 1, 1, 2, 0, 9, 1, 2, 3, 6, 4],
  							[999, 999, 1, 3, 999, 7, 4, 999, 9, 0, 4, 999, 999, 6, 2],
  							[3, 8, 999, 999, 3, 6, 7, 4, 1, 4, 0, 4, 2, 999, 4],
  							[2, 2, 4, 8, 1, 999, 4, 999, 2, 999, 4, 0, 7, 2, 999],
  							[999, 3, 2, 1, 999, 2, 999, 1, 3, 999, 2, 7, 0, 7, 4],
  							[5, 4, 7, 4, 1, 7, 1, 4, 6, 6, 999, 2, 7, 0, 8],
  							[6, 1, 999, 999, 999, 4, 5, 1, 4, 2, 4, 999, 4, 8, 0]];



  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Input placeholder = "Cuantos nodos usaras?" name="nodos"
                 onChange={e => formedit(e)}/>
               <Button type="primary" onClick = {e => Dijkstra(anchos)}  >Submit</Button>
        </Form>
        <Table columns={columns} dataSource={data} />
      </div>
    </div>
  );
}

export default Home;
