import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [numero, setNumero] = useState(-1);
  const [columns, setColumns] = useState([]);

  const colu = (e, og) =>  {
    let agregar = [
      {
        title: "Numero de Subred",
        dataIndex: "subred",
        key: 'subred',
      },
      {
        title: "Direccion de Subred",
        dataIndex: "direccion",
        key: 'direccion',
      },
      {
        title: "Primer dir IP Host",
        dataIndex: "pip",
        key: 'pip',
      },
      {
        title: "Ultima dir IP Host",
        dataIndex: "uip",
        key: 'uip',
      },
      {
        title: "Dir Brodcast",
        dataIndex: "brodcast",
        key: 'brodcast',
      },
      {
        title: "Mascara",
        dataIndex: "mascara",
        key: 'mascara',
      }

    ]
    if(og == 0){


      setColumns(agregar)
    }
    else {
      agregar.push({
        title: "funciona",
        dataIndex: "porfavor",
        key: 'ya',
      })
      setColumns(agregar)

    }
  }

  const numeros = (e)=>{
    setNumero(numero+1);
    colu(e,numero);
  }

  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Button type="primary" onClick = {e => numeros(e,1)}  >Submit</Button>
        </Form>
        <Table columns={columns} dataSource={data} />
      </div>
    </div>
  );
}

export default Home;
