import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [valido, setValido] = useState();

  let col = [
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    }

  ]
  const [columns, setColumns] = useState(col);


  const agregarCol= () =>{
    col.push({
      title:"a ver",
      dataIndex: "aver",
      key: "aver"
    })
    setColumns(col);
  }




  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Button type="primary" onClick={agregarCol} >Submit</Button>
        </Form>
        <h2>{valido}</h2>
        <Table columns={columns}/>
      </div>
    </div>
  );
}

export default Home;
