import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [valido, setValido] = useState();
  const [columns, setColumns] = useState([{
    title: "Numero de Subred",
    dataIndex: "subred",
    key: 'subred',
  },
  {
    title: "Direccion de Subred",
    dataIndex: "direccion",
    key: 'direccion',
  },
  {
    title: "Primer dir IP Host",
    dataIndex: "pip",
    key: 'pip',
  },
  {
    title: "Ultima dir IP Host",
    dataIndex: "uip",
    key: 'uip',
  },
  {
    title: "Dir Brodcast",
    dataIndex: "brodcast",
    key: 'brodcast',
  },
  {
    title: "Mascara",
    dataIndex: "mascara",
    key: 'mascara',
  }
]);

  const colu = (event) =>  {
    let agregar = columns;
    agregar.push({
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip'
    })
    setColumns(agregar);
    console.log(agregar)
  }

  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Button type="primary" onClick = {e => colu(e)}  >Submit</Button>
        </Form>
        <h2>{valido}</h2>
        <Table columns={columns} dataSource={data} />
      </div>
    </div>
  );
}

export default Home;
