import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const agregar =[
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    },
    {
      title: "Primer dir IP Host",
      dataIndex: "pip",
      key: 'pip',
    },
    {
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip',
    },
    {
      title: "Dir Brodcast",
      dataIndex: "brodcast",
      key: 'brodcast',
    },
    {
      title: "Mascara",
      dataIndex: "mascara",
      key: 'mascara',
    }

  ]
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);

  const colu = (e, og) =>  {
    if(og == 0){


      setColumns(agregar)
    }
    else {
      let empezamos = 0
      while(og != empezamos){
        agregar.push({
          title: "Nodo " + (empezamos+1),
          dataIndex: "porfavor",
          key: 'ya',
        })
        empezamos++;
      }
      setColumns(agregar)

    }
  }

  const numeros = (e)=>{
    const numeros = form.nodos;
    if(form.numeros){
      console.log(form.numeros)
      colu(e,form.nodos);

    }
  }

  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Input placeholder = "Cuantos nodos usaras?" name="nodos"
                 onChange={e => formedit(e)}/>
          <Button type="primary" onClick = {e => numeros(e)}  >Submit</Button>
        </Form>
        <Table columns={columns} dataSource={data} />
      </div>
    </div>
  );
}

export default Home;
