import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  document.title = "Calculadora de Subredes"
  const agregar =[]
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);

  const colu = (e, og) =>  {
    if(og == 0){


      setColumns(agregar)
    }
    else {
      let empezamos = 0
      while(og != empezamos){
        agregar.push({
          title: "Nodo " + (empezamos+1),
          dataIndex: "porfavor",
          key: 'ya',
          render: (porfavor, row, index) => { return <input  type="number" name="Nodo"  onChange={e => formedit(e)}/ > }
        })
        empezamos++;
      }
      setColumns(agregar)

    }
  }

  const numeros = (e)=>{
    const numeros = parseInt(form.nodos,10);
    if(numeros > 0){
      colu(e,numeros);
      crearRow(numeros)
    }
  }

  const crearRow = (nod) =>{
    let newData = [];
    for(let i = 0; i< nod; i++){
      let fila = {};
      for(let j = 0; j < nod; j++){
        fila["porfavor"] = "3";
      }
      newData.push(fila);
      console.log(newData)
    }
    setData(newData);
  }


    const camino =  (t,v) =>{
      if(t[v] != 1) {
        camino(t,t[v]);
        console.log("- ",t[v]);
      }
    }

    const Dijkstra =  (w) =>
    {    let l = new Array(w.length + 1 ),  t = new Array (w.length + 1),   r = new Array(w.length + 1),  min,  vmin;
       for (let i = 2; i<=w.length ; i++){
          l[i] = w[0][i-1];
          t[i] = 1;
        }
       for (let x = 1; x < w.length ; x++)
       {	min = 999;
          for (let i = 2; i<= w.length ; i++){
            if ( 0 <= l[i] && l[i] <= min)
              {  min = l[i]; vmin = i; }
           }
         	for (let i=2; i<= w.length ; i++){
  	        if (l[vmin]+w[vmin-1][i-1] < l[i])
               {	l[i] = l[vmin]+w[vmin-1][i-1];
  				        l[i] = vmin; }
           }
          r[vmin] = l[vmin];
          l[vmin] = -1;
       }
       console.log(  "\nCAMINOS MAS CORTOS\n");
       for (let i = 2; i <= 15; i++)
       { console.log( "de 1 a " ,i , ": 1");
         camino(t, i);
         console.log( "-", i, " cuesta " , r[i], " unidades\n");
       }
     }




  return (
    <div className="negro">
      <div className="todo">
        <h1>Bienvenido a calcular las subredes de una Direccion IP</h1>
        <h2> Cual direccion quieres usar? </h2>
        <Form layout = 'vertical'>
          <Input placeholder = "Cuantos nodos usaras?" name="nodos"
                 onChange={e => formedit(e)}/>
               <Button type="primary" onClick = {e => Dijkstra()}  >Submit</Button>
        </Form>
        <Table columns={columns} dataSource={data} />
      </div>
    </div>
  );
}

export default Home;
